import React, { useRef, useState, useEffect } from 'react';
import Webcam from 'react-webcam';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaCamera, FaTshirt, FaRedo, FaCheck, FaArrowRight } from 'react-icons/fa';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';
import useStore from './store';

// ================================
// ProgressSteps Bileşeni
// ================================
interface ProgressStep {
  step: number;
  text: string;
  icon: React.ReactNode;
}

interface ProgressStepsProps {
  currentStep: number;
}

const ProgressSteps: React.FC<ProgressStepsProps> = ({ currentStep }) => {
  const steps: ProgressStep[] = [
    { step: 1, text: "Hoş Geldiniz", icon: <FaCheck className="w-5 h-5" /> },
    { step: 2, text: "Fotoğraf Çekimi", icon: <FaCamera className="w-5 h-5" /> },
    { step: 3, text: "Kıyafet Deneme", icon: <FaTshirt className="w-5 h-5" /> }
  ];

  return (
    <div className="progress-steps mb-8">
      {steps.map((item, idx) => (
        <React.Fragment key={item.step}>
          <div className={`step-indicator ${currentStep >= item.step ? 'active' : ''}`}>
            {item.icon}
          </div>
          {idx < steps.length - 1 && (
            <div className={`step-line ${currentStep > item.step ? 'active' : ''}`}></div>
          )}
        </React.Fragment>
      ))}
    </div>
  );
};

// ================================
// Adım 1: Hoş Geldiniz / KVKK Onayı
// ================================
interface WelcomeStepProps {
  onStart: () => void;
}

const WelcomeStep: React.FC<WelcomeStepProps> = ({ onStart }) => (
  <div className="bg-white rounded-lg shadow-lg p-8 text-center">
    <h1 className="page-title mb-6">
      Akıllı Ayna Deneyimine Hoş Geldiniz
    </h1>
    <p className="page-subtitle mb-8">
      Yapay zeka destekli sanal giyinme kabini ile alışveriş deneyiminizi dönüştürün.
      Seçtiğiniz kıyafetleri anında üzerinizde görün!
    </p>
    <button onClick={onStart} className="button-primary">
      Başla <FaArrowRight />
    </button>
  </div>
);

// ================================
// Adım 2: Fotoğraf Çekimi
// ================================
interface PhotoCaptureStepProps {
  capturedImage: string | null;
  onCapture: () => void;
  onRetake: () => void;
  onVirtualTryOn: () => void;
  webcamRef: React.RefObject<Webcam>;
}

const PhotoCaptureStep: React.FC<PhotoCaptureStepProps> = ({
  capturedImage,
  onCapture,
  onRetake,
  onVirtualTryOn,
  webcamRef,
}) => (
  <div className="bg-white rounded-lg shadow-lg p-8">
    <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
      Fotoğrafınızı Çekin
    </h2>
    <div className="webcam-container mb-6">
      {capturedImage ? (
        <img
          src={capturedImage}
          alt="Captured"
          className="w-full h-full object-cover"
        />
      ) : (
        <Webcam
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          className="w-full h-full object-cover"
        />
      )}
    </div>
    <div className="flex justify-center gap-4">
      {capturedImage ? (
        <>
          <button onClick={onRetake} className="button-secondary">
            <FaRedo /> Tekrar Çek
          </button>
          <button onClick={onVirtualTryOn} className="button-primary">
            <FaTshirt /> Kıyafet Dene
          </button>
        </>
      ) : (
        <button onClick={onCapture} className="button-primary">
          <FaCamera /> Fotoğraf Çek
        </button>
      )}
    </div>
  </div>
);

// ================================
// Adım 3: Sanal Kıyafet Deneme
// ================================
interface VirtualTryOnStepProps {
  isLoading: boolean;
  virtualTryOnResult: string | null;
  onRetake: () => void;
}

const VirtualTryOnStep: React.FC<VirtualTryOnStepProps> = ({
  isLoading,
  virtualTryOnResult,
  onRetake,
}) => (
  <div className="bg-white rounded-lg shadow-lg p-8">
    <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
      İşte Yeni Görünümünüz!
    </h2>
    <div className="webcam-container mb-6">
      {isLoading ? (
        <Skeleton height="100%" />
      ) : virtualTryOnResult ? (
        <img
          src={virtualTryOnResult}
          alt="Virtual Try-On Result"
          className="result-image w-full h-full object-cover"
        />
      ) : null}
    </div>
    <div className="flex justify-center gap-4">
      <button onClick={onRetake} className="button-primary">
        <FaCamera /> Yeni Fotoğraf Çek
      </button>
    </div>
  </div>
);

// ================================
// Ana Bileşen: App
// ================================
const App: React.FC = () => {
  const { step, setStep } = useStore();
  const webcamRef = useRef<Webcam>(null);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const [virtualTryOnResult, setVirtualTryOnResult] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  // Uygulama ilk yüklendiğinde adım 0 ise 1'e çekiyoruz.
  useEffect(() => {
    if (step === 0) {
      setStep(1);
    }
  }, [step, setStep]);

  // Kameradan fotoğraf çekme
  const capturePhoto = async () => {
    const imageSrc = webcamRef.current?.getScreenshot();
    if (!imageSrc) {
      toast.error('Fotoğraf çekilemedi. Lütfen kameranızı kontrol edin.');
      return;
    }
    setCapturedImage(imageSrc);
    setStep(2);
  };

  // Sanal kıyafet deneme işlemi
  const performVirtualTryOn = async () => {
    if (!capturedImage) return;
    setIsLoading(true);
    try {
      const blob = await fetch(capturedImage).then(res => res.blob());
      const formData = new FormData();
      formData.append('file', blob, 'photo.jpg');

      const response = await fetch('http://localhost:8000/api/virtual-tryon', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Virtual try on işlemi başarısız oldu');
      }

      const blobResult = await response.blob();
      const base64Result = await new Promise<string>((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(blobResult);
        reader.onloadend = () => resolve(reader.result as string);
        reader.onerror = error => reject(error);
      });

      setVirtualTryOnResult(base64Result);
      setStep(3);
      toast.success('Kıyafet başarıyla denendi!');
    } catch (error) {
      toast.error('Virtual try on işlemi sırasında bir hata oluştu');
      console.error("Virtual try on hatası:", error);
    } finally {
      setIsLoading(false);
    }
  };

  // Fotoğrafı yeniden çekmek için resetleme
  const handleRetakePhoto = () => {
    setCapturedImage(null);
    setVirtualTryOnResult(null);
    setStep(2);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-gray-200">
      <ToastContainer position="top-right" autoClose={3000} />
      <div className="max-w-6xl mx-auto px-4 py-8">
        <ProgressSteps currentStep={step} />
        <div className="max-w-4xl mx-auto">
          {step === 1 && (
            <WelcomeStep onStart={() => setStep(2)} />
          )}

          {step === 2 && (
            <PhotoCaptureStep
              capturedImage={capturedImage}
              onCapture={capturePhoto}
              onRetake={() => setCapturedImage(null)}
              onVirtualTryOn={performVirtualTryOn}
              webcamRef={webcamRef}
            />
          )}

          {step === 3 && (
            <VirtualTryOnStep
              isLoading={isLoading}
              virtualTryOnResult={virtualTryOnResult}
              onRetake={handleRetakePhoto}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
