import React, { useRef, useState } from 'react';
import Webcam from 'react-webcam';
import useStore from './store.ts';
import { motion, AnimatePresence } from 'framer-motion';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaCamera, FaTshirt, FaRedo, FaCheck, FaArrowRight, FaMagic } from 'react-icons/fa';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';

const App: React.FC = () => {
  const { step, setStep } = useStore();
  const webcamRef = useRef<Webcam>(null);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const [virtualTryOnResult, setVirtualTryOnResult] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const capturePhoto = async () => {
    const imageSrc = webcamRef.current?.getScreenshot();
    if (!imageSrc) {
      toast.error('Fotoğraf çekilemedi. Lütfen kameranızı kontrol edin.');
      return;
    }
    setCapturedImage(imageSrc);
    setStep(2);
  };

  const performVirtualTryOn = async () => {
    if (!capturedImage) return;
    setIsLoading(true);
    try {
      const blob = await fetch(capturedImage).then(res => res.blob());
      const formData = new FormData();
      formData.append('file', blob, 'photo.jpg');

      const response = await fetch('http://localhost:8000/api/virtual-tryon', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Virtual try on işlemi başarısız oldu');
      }

      const blobResult = await response.blob();
      const base64Result = await new Promise<string>((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(blobResult);
        reader.onloadend = () => resolve(reader.result as string);
        reader.onerror = error => reject(error);
      });

      setVirtualTryOnResult(base64Result);
      setStep(3);
      toast.success('Kıyafet başarıyla denendi!');
    } catch (error) {
      toast.error('Virtual try on işlemi sırasında bir hata oluştu');
      console.error("Virtual try on hatası:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const containerVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
    exit: { opacity: 0, y: -20, transition: { duration: 0.3 } }
  };

  return (
    <div className="min-h-screen py-12 px-4 sm:px-6 lg:px-8" style={{ background: 'var(--background-gradient)' }}>
      <ToastContainer 
        position="top-right" 
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />

      <div className="max-w-7xl mx-auto">
        {/* Progress Steps */}
        <div className="mb-12 max-w-3xl mx-auto">
          <div className="flex items-center justify-between">
            {[
              { num: 1, text: "KVKK Onayı" },
              { num: 2, text: "Fotoğraf Çekimi" },
              { num: 3, text: "Kıyafet Deneme" }
            ].map((item, index) => (
              <div key={item.num} className="flex items-center">
                <div className="flex flex-col items-center">
                  <div className={`step-indicator ${step >= item.num - 1 ? 'active' : 'bg-gray-200'}`}>
                    {item.num}
                  </div>
                  <span className="mt-2 text-sm font-medium text-gray-600">{item.text}</span>
                </div>
                {index < 2 && (
                  <div className="w-24 mx-4">
                    <div className="progress-bar">
                      <div 
                        className="progress-bar-fill" 
                        style={{ 
                          width: step >= item.num ? '100%' : '0%',
                          opacity: step >= item.num ? 1 : 0.3
                        }}
                      />
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>

        <AnimatePresence mode="wait">
          {step === 0 && (
            <motion.div
              key="welcome"
              variants={containerVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
              className="glass-effect p-8 sm:p-12"
            >
              <div className="max-w-4xl mx-auto text-center">
                <h1 className="text-4xl sm:text-5xl lg:text-6xl font-extrabold mb-8 gradient-text">
                  Akıllı Ayna Deneyimine Hoş Geldiniz
                </h1>
                <div className="mb-12">
                  <p className="text-lg sm:text-xl text-gray-700 mb-8 leading-relaxed">
                    Yapay zeka destekli sanal giyinme kabini ile alışveriş deneyiminizi dönüştürün.
                    Seçtiğiniz kıyafetleri anında üzerinizde görün!
                  </p>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    {[
                      { icon: FaCamera, title: "Fotoğraf Çekin", desc: "Yüksek kaliteli fotoğrafınızı çekin" },
                      { icon: FaMagic, title: "AI İşlemi", desc: "Yapay zeka kıyafeti üzerinize uygular" },
                      { icon: FaCheck, title: "Sonucu Görün", desc: "Kıyafetin size nasıl yakıştığını inceleyin" }
                    ].map(({ icon: Icon, title, desc }) => (
                      <div key={title} className="card">
                        <div className="w-12 h-12 mx-auto mb-4 rounded-xl bg-indigo-100 flex items-center justify-center">
                          <Icon className="w-6 h-6 text-indigo-600" />
                        </div>
                        <h3 className="text-xl font-semibold mb-2">{title}</h3>
                        <p className="text-gray-600">{desc}</p>
                      </div>
                    ))}
                  </div>
                </div>
                <motion.button
                  className="button-primary group"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => setStep(1)}
                >
                  <span className="text-lg">Hemen Başla</span>
                  <FaArrowRight className="w-5 h-5 group-hover:translate-x-1 transition-transform" />
                </motion.button>
              </div>
            </motion.div>
          )}

          {step === 1 && (
            <motion.div
              key="capture"
              variants={containerVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
              className="glass-effect p-8 sm:p-12"
            >
              <div className="max-w-5xl mx-auto">
                <h2 className="text-3xl sm:text-4xl font-bold text-center mb-8 gradient-text">
                  En İyi Pozunuzu Verin
                </h2>
                <div className="webcam-container mb-8">
                  <Webcam
                    audio={false}
                    ref={webcamRef}
                    screenshotFormat="image/jpeg"
                    className="w-full h-full object-cover"
                    videoConstraints={{
                      width: 1280,
                      height: 720,
                      facingMode: "user"
                    }}
                  />
                </div>
                <div className="flex justify-center">
                  <motion.button
                    className="button-primary group"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={capturePhoto}
                  >
                    <FaCamera className="w-6 h-6" />
                    <span className="text-lg">Fotoğraf Çek</span>
                  </motion.button>
                </div>
              </div>
            </motion.div>
          )}

          {step === 2 && (
            <motion.div
              key="try-on"
              variants={containerVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
              className="glass-effect p-8 sm:p-12"
            >
              <div className="max-w-6xl mx-auto">
                <h2 className="text-3xl sm:text-4xl font-bold text-center mb-8 gradient-text">
                  Kıyafet Denemesi
                </h2>
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-start">
                  <div>
                    <div className="relative">
                      {capturedImage && (
                        <img
                          src={capturedImage}
                          alt="Captured"
                          className="result-image w-full"
                        />
                      )}
                      <div className="absolute top-4 left-4 bg-white/90 backdrop-blur-sm rounded-lg px-3 py-1.5">
                        <span className="text-sm font-medium text-gray-700">Orijinal Fotoğraf</span>
                      </div>
                    </div>
                  </div>
                  <div className="lg:sticky lg:top-8">
                    <div className="card space-y-6">
                      <h3 className="text-2xl font-semibold gradient-text">Kıyafet Seçimi</h3>
                      <p className="text-gray-600">
                        Fotoğrafınız hazır! Şimdi AI modelimiz seçtiğiniz kıyafeti üzerinize uygulayacak.
                        Bu işlem birkaç saniye sürebilir.
                      </p>
                      <motion.button
                        className="button-primary w-full group"
                        whileHover={{ scale: 1.02 }}
                        whileTap={{ scale: 0.98 }}
                        onClick={performVirtualTryOn}
                        disabled={isLoading}
                      >
                        {isLoading ? (
                          <div className="flex items-center justify-center gap-3">
                            <div className="loading-spinner" />
                            <span>İşleniyor...</span>
                          </div>
                        ) : (
                          <>
                            <FaTshirt className="w-6 h-6" />
                            <span className="text-lg">Kıyafeti Dene</span>
                          </>
                        )}
                      </motion.button>
                    </div>
                  </div>
                </div>
              </div>
            </motion.div>
          )}

          {step === 3 && (
            <motion.div
              key="result"
              variants={containerVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
              className="glass-effect p-8 sm:p-12"
            >
              <div className="max-w-6xl mx-auto">
                <h2 className="text-3xl sm:text-4xl font-bold text-center mb-8 gradient-text">
                  İşte Yeni Görünümünüz!
                </h2>
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-start">
                  <div className="space-y-8">
                    <div className="relative">
                      {virtualTryOnResult ? (
                        <img
                          src={virtualTryOnResult}
                          alt="Virtual Try On Result"
                          className="result-image w-full"
                        />
                      ) : (
                        <Skeleton height={600} />
                      )}
                      <div className="absolute top-4 left-4 bg-white/90 backdrop-blur-sm rounded-lg px-3 py-1.5">
                        <span className="text-sm font-medium text-gray-700">Kıyafet Denenmiş Hali</span>
                      </div>
                    </div>
                  </div>
                  <div className="lg:sticky lg:top-8">
                    <div className="card space-y-6">
                      <h3 className="text-2xl font-semibold gradient-text">Harika Görünüyor!</h3>
                      <p className="text-gray-600">
                        Kıyafet denemesi tamamlandı. Başka bir kıyafet denemek veya yeni bir fotoğraf çekmek ister misiniz?
                      </p>
                      <div className="grid grid-cols-2 gap-4">
                        <motion.button
                          className="button-primary group"
                          whileHover={{ scale: 1.02 }}
                          whileTap={{ scale: 0.98 }}
                          onClick={() => setStep(1)}
                        >
                          <FaCamera className="w-5 h-5" />
                          <span>Yeni Fotoğraf</span>
                        </motion.button>
                        <motion.button
                          className="button-secondary group"
                          whileHover={{ scale: 1.02 }}
                          whileTap={{ scale: 0.98 }}
                          onClick={() => setStep(2)}
                        >
                          <FaTshirt className="w-5 h-5" />
                          <span>Başka Kıyafet</span>
                        </motion.button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default App;
