import React, { useRef, useState, useEffect, useCallback } from 'react';
import Webcam from 'react-webcam';
import useStore from './store';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaCamera, FaTshirt, FaRedo, FaCheck, FaArrowRight } from 'react-icons/fa';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';

const App: React.FC = () => {
  const { step, setStep } = useStore();
  const webcamRef = useRef<Webcam>(null);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const [virtualTryOnResult, setVirtualTryOnResult] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  // Blob'dan Base64'e dönüştürme yardımcı fonksiyonu
  const blobToBase64 = (blob: Blob): Promise<string> =>
    new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(blob);
      reader.onloadend = () => resolve(reader.result as string);
      reader.onerror = error => reject(error);
    });

  // İlk renderda adım 0 ise 1'e geçiş yapıyoruz.
  useEffect(() => {
    if (step === 0) {
      setStep(1);
    }
  }, [step, setStep]);

  // Fotoğraf çekme işlemi
  const capturePhoto = useCallback(async () => {
    const imageSrc = webcamRef.current?.getScreenshot();
    if (!imageSrc) {
      toast.error('Fotoğraf çekilemedi. Lütfen kameranızı kontrol edin.');
      return;
    }
    setCapturedImage(imageSrc);
    setStep(2);
  }, [setStep]);

  // Sanal deneme işlemi
  const performVirtualTryOn = useCallback(async () => {
    if (!capturedImage) return;
    setIsLoading(true);
    try {
      const blob = await fetch(capturedImage).then(res => res.blob());
      const formData = new FormData();
      formData.append('file', blob, 'photo.jpg');

      const response = await fetch('http://localhost:8000/api/virtual-tryon', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Virtual try on işlemi başarısız oldu');
      }

      const blobResult = await response.blob();
      const base64Result = await blobToBase64(blobResult);

      setVirtualTryOnResult(base64Result);
      setStep(3);
      toast.success('Kıyafet başarıyla denendi!');
    } catch (error) {
      toast.error('Virtual try on işlemi sırasında bir hata oluştu');
      console.error("Virtual try on hatası:", error);
    } finally {
      setIsLoading(false);
    }
  }, [capturedImage, setStep]);

  // İlerleme adımlarını gösteren bileşen
  const ProgressSteps: React.FC = () => {
    const steps = [
      { step: 1, text: "KVKK Onayı", icon: <FaCheck className="w-5 h-5" /> },
      { step: 2, text: "Fotoğraf Çekimi", icon: <FaCamera className="w-5 h-5" /> },
      { step: 3, text: "Kıyafet Deneme", icon: <FaTshirt className="w-5 h-5" /> },
    ];

    return (
      <div className="progress-steps mb-8 flex items-center justify-center">
        {steps.map((item, idx) => (
          <React.Fragment key={item.step}>
            <div className={`step-indicator ${step >= item.step ? 'active' : ''}`}>
              {item.icon}
            </div>
            {idx < steps.length - 1 && (
              <div className={`step-line ${step > item.step ? 'active' : ''}`}></div>
            )}
          </React.Fragment>
        ))}
      </div>
    );
  };

  // Adım 1: Hoş Geldiniz Ekranı
  const WelcomeScreen: React.FC = () => (
    <div className="text-center">
      <h1 className="page-title mb-4">Akıllı Ayna Deneyimine Hoş Geldiniz</h1>
      <p className="page-subtitle mb-8">
        Yapay zeka destekli sanal giyinme kabini ile alışveriş deneyiminizi dönüştürün.
        Seçtiğiniz kıyafetleri anında üzerinizde görün!
      </p>
      <button
        onClick={() => setStep(2)}
        className="button-primary mx-auto"
        aria-label="Başla"
      >
        Başla <FaArrowRight />
      </button>
    </div>
  );

  // Adım 2: Fotoğraf Çekme Ekranı
  const CaptureScreen: React.FC = () => (
    <div>
      <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">
        Fotoğrafınızı Çekin
      </h2>
      <div className="webcam-container mb-6">
        {capturedImage ? (
          <img
            src={capturedImage}
            alt="Çekilen Fotoğraf"
            className="w-full h-full object-cover"
          />
        ) : (
          <Webcam
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            className="w-full h-full object-cover"
          />
        )}
      </div>
      <div className="flex justify-center gap-4">
        {capturedImage ? (
          <>
            <button
              onClick={() => setCapturedImage(null)}
              className="button-secondary"
              aria-label="Tekrar Çek"
            >
              <FaRedo /> Tekrar Çek
            </button>
            <button
              onClick={performVirtualTryOn}
              className="button-primary"
              aria-label="Kıyafet Dene"
              disabled={isLoading}
            >
              <FaTshirt /> Kıyafet Dene
            </button>
          </>
        ) : (
          <button
            onClick={capturePhoto}
            className="button-primary mx-auto"
            aria-label="Fotoğraf Çek"
          >
            <FaCamera /> Fotoğraf Çek
          </button>
        )}
      </div>
    </div>
  );

  // Adım 3: Sanal Deneme Sonucu Ekranı
  const TryOnResultScreen: React.FC = () => (
    <div>
      <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">
        İşte Yeni Görünümünüz!
      </h2>
      <div className="webcam-container mb-6">
        {isLoading ? (
          <Skeleton height="100%" className="w-full h-full" />
        ) : virtualTryOnResult ? (
          <img
            src={virtualTryOnResult}
            alt="Sanal Deneme Sonucu"
            className="w-full h-full object-cover result-image"
          />
        ) : null}
      </div>
      <div className="flex justify-center">
        <button
          onClick={() => {
            setCapturedImage(null);
            setVirtualTryOnResult(null);
            setStep(2);
          }}
          className="button-primary"
          aria-label="Yeni Fotoğraf Çek"
        >
          <FaCamera /> Yeni Fotoğraf Çek
        </button>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-gray-200 flex items-center justify-center p-4">
      <ToastContainer position="top-right" autoClose={3000} />
      <div className="max-w-4xl w-full">
        <div className="glass-effect p-8 rounded-3xl">
          <ProgressSteps />
          {step === 1 && <WelcomeScreen />}
          {step === 2 && <CaptureScreen />}
          {step === 3 && <TryOnResultScreen />}
        </div>
      </div>
    </div>
  );
};

export default App;
