import React, { useRef, useState, useCallback } from 'react';
import Webcam from 'react-webcam';
import useStore from './store.ts';
import { motion, AnimatePresence } from 'framer-motion';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaCamera, FaTshirt, FaRedo, FaCheck, FaArrowRight, FaMagic, FaSpinner } from 'react-icons/fa';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';

const App: React.FC = () => {
  const { step, setStep } = useStore();
  const webcamRef = useRef<Webcam>(null);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const [virtualTryOnResult, setVirtualTryOnResult] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedClothing, setSelectedClothing] = useState<string | null>(null);

  const capturePhoto = useCallback(async () => {
    const imageSrc = webcamRef.current?.getScreenshot();
    if (!imageSrc) {
      toast.error('FotoÄŸraf Ã§ekilemedi. LÃ¼tfen kameranÄ±zÄ± kontrol edin.');
      return;
    }
    setCapturedImage(imageSrc);
    setStep(2);

    // Capture success animation and sound
    new Audio('/camera-shutter.mp3').play().catch(() => {});
    toast.success('FotoÄŸraf baÅŸarÄ±yla Ã§ekildi!');
  }, [setStep]);

  const performVirtualTryOn = async () => {
    if (!capturedImage || !selectedClothing) {
      toast.warning('LÃ¼tfen Ã¶nce bir kÄ±yafet seÃ§in!');
      return;
    }

    setIsLoading(true);
    try {
      const blob = await fetch(capturedImage).then(res => res.blob());
      const formData = new FormData();
      formData.append('file', blob, 'photo.jpg');
      formData.append('clothing', selectedClothing);

      const response = await fetch('http://localhost:8000/api/virtual-tryon', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Virtual try on iÅŸlemi baÅŸarÄ±sÄ±z oldu');
      }

      const blobResult = await response.blob();
      const base64Result = await new Promise<string>((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(blobResult);
        reader.onloadend = () => resolve(reader.result as string);
        reader.onerror = error => reject(error);
      });

      setVirtualTryOnResult(base64Result);
      setStep(3);
      toast.success('KÄ±yafet baÅŸarÄ±yla denendi!', {
        icon: "ðŸŽ‰"
      });
    } catch (error) {
      toast.error('Virtual try on iÅŸlemi sÄ±rasÄ±nda bir hata oluÅŸtu');
      console.error("Virtual try on hatasÄ±:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const containerVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { 
        duration: 0.5,
        type: "spring",
        stiffness: 100
      } 
    },
    exit: { 
      opacity: 0, 
      y: -20, 
      transition: { 
        duration: 0.3,
        ease: "easeInOut"
      } 
    }
  };

  const clothingOptions = [
    { id: 'tshirt1', name: 'Basic T-Shirt', image: '/clothes/tshirt1.png' },
    { id: 'tshirt2', name: 'V-Neck T-Shirt', image: '/clothes/tshirt2.png' },
    { id: 'jacket1', name: 'Denim Jacket', image: '/clothes/jacket1.png' },
  ];

  return (
    <div className="min-h-screen py-12 px-4 sm:px-6 lg:px-8 bg-gradient-to-br from-indigo-50 to-purple-50">
      <ToastContainer 
        position="top-right" 
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />

      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <motion.div 
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-center mb-12"
        >
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            Virtual Fitting Room
          </h1>
          <p className="text-lg text-gray-600">
            KÄ±yafetleri gerÃ§ek zamanlÄ± olarak Ã¼zerinizde deneyin
          </p>
        </motion.div>

        {/* Progress Steps */}
        <div className="mb-12 max-w-3xl mx-auto">
          <div className="flex items-center justify-between">
            {[
              { num: 1, text: "FotoÄŸraf Ã‡ekimi" },
              { num: 2, text: "KÄ±yafet SeÃ§imi" },
              { num: 3, text: "SonuÃ§" }
            ].map((item, index) => (
              <div key={item.num} className="flex items-center">
                <motion.div 
                  className="flex flex-col items-center"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.2 }}
                >
                  <div className={`step-indicator ${step >= item.num ? 'active' : ''}`}>
                    {step > item.num ? <FaCheck /> : item.num}
                  </div>
                  <span className="mt-2 text-sm font-medium text-gray-600">{item.text}</span>
                </motion.div>
                {index < 2 && (
                  <div className="w-24 h-1 mx-4 bg-gray-200 rounded-full">
                    <motion.div 
                      className="h-full bg-indigo-600 rounded-full"
                      initial={{ width: "0%" }}
                      animate={{ width: step > item.num ? "100%" : "0%" }}
                      transition={{ duration: 0.5 }}
