import React, { useRef, useState, useEffect } from 'react';
import Webcam from 'react-webcam';
import useStore from './store';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaCamera, FaTshirt, FaRedo, FaCheck, FaArrowRight } from 'react-icons/fa';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';

// --- İlerleme Adımları için Tip Tanımı ---
interface ProgressStep {
  step: number;
  label: string;
  icon: React.ReactNode;
}

const progressSteps: ProgressStep[] = [
  { step: 1, label: "KVKK Onayı", icon: <FaCheck className="w-5 h-5" /> },
  { step: 2, label: "Fotoğraf Çekimi", icon: <FaCamera className="w-5 h-5" /> },
  { step: 3, label: "Kıyafet Deneme", icon: <FaTshirt className="w-5 h-5" /> },
];

// --- İlerleme Göstergesi Bileşeni ---
const ProgressIndicator: React.FC<{ currentStep: number }> = ({ currentStep }) => {
  return (
    <div className="mb-8 flex items-center justify-center gap-4">
      {progressSteps.map((item, idx) => (
        <React.Fragment key={item.step}>
          <div
            className={`flex flex-col items-center ${
              currentStep >= item.step ? 'text-indigo-600' : 'text-gray-400'
            }`}
          >
            <div
              className={`w-12 h-12 rounded-full flex items-center justify-center mb-2 transition-all ${
                currentStep >= item.step
                  ? 'bg-indigo-600 text-white shadow-lg'
                  : 'bg-gray-200 text-gray-400'
              }`}
            >
              {item.icon}
            </div>
            <span className="text-sm font-medium">{item.label}</span>
          </div>
          {idx < progressSteps.length - 1 && (
            <div
              className={`h-1 w-16 rounded transition-all ${
                currentStep > item.step ? 'bg-indigo-600' : 'bg-gray-200'
              }`}
            />
          )}
        </React.Fragment>
      ))}
    </div>
  );
};

// --- Adım 1: Hoş Geldiniz / KVKK Onayı ---
const WelcomeStep: React.FC<{ onNext: () => void }> = ({ onNext }) => (
  <div className="bg-white rounded-lg shadow-lg p-8 text-center">
    <h1 className="text-3xl font-bold text-gray-800 mb-6">
      Akıllı Ayna Deneyimine Hoş Geldiniz
    </h1>
    <p className="text-gray-600 mb-8">
      Yapay zeka destekli sanal giyinme kabini ile alışveriş deneyiminizi dönüştürün.
      Seçtiğiniz kıyafetleri anında üzerinizde görün!
    </p>
    <button
      onClick={onNext}
      className="bg-indigo-600 text-white px-8 py-3 rounded-lg font-medium
                 hover:bg-indigo-700 transition-colors flex items-center justify-center gap-2
                 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
    >
      Başla <FaArrowRight />
    </button>
  </div>
);

// --- Adım 2: Fotoğraf Çekimi ---
interface CaptureStepProps {
  capturedImage: string | null;
  onCapture: () => void;
  onRetake: () => void;
  onTryOn: () => void;
  webcamRef: React.RefObject<Webcam>;
}

const CaptureStep: React.FC<CaptureStepProps> = ({
  capturedImage,
  onCapture,
  onRetake,
  onTryOn,
  webcamRef,
}) => (
  <div className="bg-white rounded-lg shadow-lg p-8">
    <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
      Fotoğrafınızı Çekin
    </h2>
    <div className="aspect-video rounded-lg overflow-hidden bg-gray-100 mb-6 shadow-inner">
      {capturedImage ? (
        <img
          src={capturedImage}
          alt="Captured"
          className="w-full h-full object-cover"
        />
      ) : (
        <Webcam
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          className="w-full h-full object-cover"
        />
      )}
    </div>
    <div className="flex justify-center gap-4">
      {capturedImage ? (
        <>
          <button
            onClick={onRetake}
            className="px-6 py-3 border-2 border-gray-300 rounded-lg font-medium
                       hover:border-gray-400 transition-colors flex items-center gap-2
                       hover:bg-gray-50"
          >
            <FaRedo /> Tekrar Çek
          </button>
          <button
            onClick={onTryOn}
            className="bg-indigo-600 text-white px-6 py-3 rounded-lg font-medium
                       hover:bg-indigo-700 transition-colors flex items-center gap-2
                       shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
          >
            <FaTshirt /> Kıyafet Dene
          </button>
        </>
      ) : (
        <button
          onClick={onCapture}
          className="bg-indigo-600 text-white px-8 py-3 rounded-lg font-medium
                     hover:bg-indigo-700 transition-colors flex items-center gap-2
                     shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
        >
          <FaCamera /> Fotoğraf Çek
        </button>
      )}
    </div>
  </div>
);

// --- Adım 3: Sanal Kıyafet Deneme ---
interface TryOnStepProps {
  isLoading: boolean;
  resultImage: string | null;
  onRetake: () => void;
}

const TryOnStep: React.FC<TryOnStepProps> = ({
  isLoading,
  resultImage,
  onRetake,
}) => (
  <div className="bg-white rounded-lg shadow-lg p-8">
    <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
      İşte Yeni Görünümünüz!
    </h2>
    <div className="aspect-video rounded-lg overflow-hidden bg-gray-100 mb-6 shadow-inner">
      {isLoading ? (
        <Skeleton height="100%" />
      ) : resultImage ? (
        <img
          src={resultImage}
          alt="Virtual Try-On Result"
          className="w-full h-full object-cover"
        />
      ) : null}
    </div>
    <div className="flex justify-center gap-4">
      <button
        onClick={onRetake}
        className="bg-indigo-600 text-white px-8 py-3 rounded-lg font-medium
                   hover:bg-indigo-700 transition-colors flex items-center gap-2
                   shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
      >
        <FaCamera /> Yeni Fotoğraf Çek
      </button>
    </div>
  </div>
);

// --- Ana Bileşen: App ---
const App: React.FC = () => {
  const { step, setStep } = useStore();
  const webcamRef = useRef<Webcam>(null);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const [virtualTryOnResult, setVirtualTryOnResult] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  // Uygulama yüklendiğinde step 0 ise 1'e çekiyoruz.
  useEffect(() => {
    if (step === 0) {
      setStep(1);
    }
  }, [step, setStep]);

  // Kameradan fotoğraf çekme
  const capturePhoto = async () => {
    const imageSrc = webcamRef.current?.getScreenshot();
    if (!imageSrc) {
      toast.error('Fotoğraf çekilemedi. Lütfen kameranızı kontrol edin.');
      return;
    }
    setCapturedImage(imageSrc);
    setStep(2);
  };

  // Sanal kıyafet deneme işlemi
  const performVirtualTryOn = async () => {
    if (!capturedImage) return;
    setIsLoading(true);
    try {
      const blob = await fetch(capturedImage).then(res => res.blob());
      const formData = new FormData();
      formData.append('file', blob, 'photo.jpg');

      const response = await fetch('http://localhost:8000/api/virtual-tryon', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Virtual try on işlemi başarısız oldu');
      }

      const blobResult = await response.blob();
      const base64Result = await new Promise<string>((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(blobResult);
        reader.onloadend = () => resolve(reader.result as string);
        reader.onerror = error => reject(error);
      });

      setVirtualTryOnResult(base64Result);
      setStep(3);
      toast.success('Kıyafet başarıyla denendi!');
    } catch (error) {
      toast.error('Virtual try on işlemi sırasında bir hata oluştu');
      console.error("Virtual try on hatası:", error);
    } finally {
      setIsLoading(false);
    }
  };

  // Fotoğrafı yeniden çekmek için resetleme işlemi
  const resetToCapture = () => {
    setCapturedImage(null);
    setVirtualTryOnResult(null);
    setStep(2);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-gray-200">
      <ToastContainer position="top-right" autoClose={3000} />
      <div className="max-w-6xl mx-auto px-4 py-8">
        <ProgressIndicator currentStep={step} />
        <div className="max-w-4xl mx-auto">
          {step === 1 && <WelcomeStep onNext={() => setStep(2)} />}
          {step === 2 && (
            <CaptureStep
              capturedImage={capturedImage}
              onCapture={capturePhoto}
              onRetake={() => setCapturedImage(null)}
              onTryOn={performVirtualTryOn}
              webcamRef={webcamRef}
            />
          )}
          {step === 3 && (
            <TryOnStep
              isLoading={isLoading}
              resultImage={virtualTryOnResult}
              onRetake={resetToCapture}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
