sourcesContent:
import axios from "axios";

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || "http://localhost:8000",
  headers: {
    "Content-Type": "application/json",
  },
  timeout: 30000, // Timeout'u 30 saniyeye çıkaralım
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    // İstek öncesi yapılacak işlemler
    return config;
  },
  (error) => {
    console.error('Request error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    if (!error.response) {
      // Network error
      console.error('Network error:', error);
      return Promise.reject(new Error('Ağ bağlantısı hatası. Lütfen internet bağlantınızı kontrol edin.'));
    }

    switch (error.response.status) {
      case 400:
        console.error('Bad request:', error.response.data);
        return Promise.reject(new Error('Geçersiz istek. Lütfen tekrar deneyin.'));
      case 401:
        console.error('Unauthorized:', error.response.data);
        return Promise.reject(new Error('Yetkisiz erişim.'));
      case 404:
        console.error('Not found:', error.response.data);
        return Promise.reject(new Error('İstenilen kaynak bulunamadı.'));
      case 500:
        console.error('Server error:', error.response.data);
        return Promise.reject(new Error('Sunucu hatası. Lütfen daha sonra tekrar deneyin.'));
      default:
        console.error('API error:', error.response.data);
        return Promise.reject(new Error('Bir hata oluştu. Lütfen tekrar deneyin.'));
    }
  }
);

export default api;
