import React, { Component, ErrorInfo, ReactNode } from "react";
import { toast } from "react-toastify";

interface Props {
  children: ReactNode;
}

interface State {
  hasError: boolean;
  error: Error | null;
  errorInfo: ErrorInfo | null;
}

class ErrorBoundary extends Component<Props, State> {
  public state: State = {
    hasError: false,
    error: null,
    errorInfo: null,
  };

  public static getDerivedStateFromError(error: Error): State {
    return {
      hasError: true,
      error,
      errorInfo: null,
    };
  }

  public componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    console.error("Uncaught error:", error, errorInfo);

    // Hata bilgisini state'e kaydet
    this.setState({
      error,
      errorInfo,
    });

    // Kullanıcıya bildirim göster
    toast.error("Bir hata oluştu. Sayfa yenileniyor...");

    // 3 saniye sonra sayfayı yenile
    setTimeout(() => {
      window.location.reload();
    }, 3000);
  }

  public render() {
    if (this.state.hasError) {
      return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
          <div className="bg-white p-8 rounded-lg shadow-xl max-w-md w-full">
            <h1 className="text-2xl font-bold text-red-600 mb-4">
              Bir şeyler yanlış gitti
            </h1>
            <p className="text-gray-600 mb-6">
              Uygulama bir hata ile karşılaştı. Sayfa otomatik olarak yenilenecek.
            </p>
            <div className="flex justify-center">
              <button
                onClick={() => window.location.reload()}
                className="bg-indigo-600 text-white px-6 py-2 rounded-lg hover:bg-indigo-700 
                         transition-colors shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
              >
                Şimdi Yenile
              </button>
            </div>
            {process.env.NODE_ENV === "development" && this.state.error && (
              <div className="mt-6 p-4 bg-gray-100 rounded-lg overflow-auto max-h-48">
                <p className="text-sm font-mono text-gray-700 whitespace-pre-wrap">
                  {this.state.error.toString()}
                </p>
                {this.state.errorInfo && (
                  <pre className="text-xs font-mono text-gray-600 mt-2 whitespace-pre-wrap">
                    {this.state.errorInfo.componentStack}
                  </pre>
                )}
              </div>
            )}
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;
