import React, { useRef, useState } from 'react';
import Webcam from 'react-webcam';
import useStore from './store.ts';
import { motion, AnimatePresence } from 'framer-motion';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaCamera, FaTshirt, FaRedo, FaCheck, FaArrowRight, FaMagic } from 'react-icons/fa';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';

const App: React.FC = () => {
  const { step, setStep } = useStore();
  const webcamRef = useRef<Webcam>(null);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const [virtualTryOnResult, setVirtualTryOnResult] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const capturePhoto = async () => {
    const imageSrc = webcamRef.current?.getScreenshot();
    if (!imageSrc) {
      toast.error('Fotoğraf çekilemedi. Lütfen kameranızı kontrol edin.');
      return;
    }
    setCapturedImage(imageSrc);
    setStep(2);
  };

  const performVirtualTryOn = async () => {
    if (!capturedImage) return;
    setIsLoading(true);
    try {
      const blob = await fetch(capturedImage).then(res => res.blob());
      const formData = new FormData();
      formData.append('file', blob, 'photo.jpg');

      const response = await fetch('http://localhost:8000/api/virtual-tryon', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Virtual try on işlemi başarısız oldu');
      }

      const blobResult = await response.blob();
      const base64Result = await new Promise<string>((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(blobResult);
        reader.onloadend = () => resolve(reader.result as string);
        reader.onerror = error => reject(error);
      });

      setVirtualTryOnResult(base64Result);
      setStep(3);
      toast.success('Kıyafet başarıyla denendi!');
    } catch (error) {
      toast.error('Virtual try on işlemi sırasında bir hata oluştu');
      console.error("Virtual try on hatası:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const containerVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
    exit: { opacity: 0, y: -20, transition: { duration: 0.3 } }
  };

  return (
    <div className="min-h-screen">
      <ToastContainer 
        position="top-right" 
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />

      <main className="main-container">
        <div className="progress-steps">
          {[
            { num: 1, text: "KVKK Onayı", icon: <FaCheck /> },
            { num: 2, text: "Fotoğraf Çekimi", icon: <FaCamera /> },
            { num: 3, text: "Kıyafet Deneme", icon: <FaTshirt /> }
          ].map((item, index) => (
            <React.Fragment key={item.num}>
              <div className="flex flex-col items-center">
                <div className={`step-indicator ${step >= item.num ? 'active' : ''}`}>
                  {item.icon}
                </div>
                <span className="step-text">{item.text}</span>
              </div>
              {index < 2 && (
                <div className={`step-line ${step > item.num ? 'active' : ''}`} />
              )}
            </React.Fragment>
          ))}
        </div>

        <AnimatePresence mode="wait">
          <motion.div
            key={step}
            variants={containerVariants}
            initial="hidden"
            animate="visible"
            exit="exit"
            className="content-section"
          >
            {step === 1 && (
              <>
                <h1 className="page-title">Akıllı Ayna Deneyimine Hoş Geldiniz</h1>
                <p className="page-subtitle">
                  Yapay zeka destekli sanal giyinme kabini ile alışveriş deneyiminizi dönüştürün. 
                  Seçtiğiniz kıyafetleri anında üzerinizde görün!
                </p>
                <div className="flex justify-center">
                  <button
                    onClick={() => setStep(2)}
                    className="button-primary"
                  >
                    Başla <FaArrowRight />
                  </button>
                </div>
              </>
            )}

            {step === 2 && (
              <>
                <h2 className="page-title">Fotoğraf Çekin</h2>
                <p className="page-subtitle">
                  Yüksek kaliteli fotoğrafınızı çekin
                </p>
                <div className="webcam-container mb-8">
                  {capturedImage ? (
                    <img
                      src={capturedImage}
                      alt="Captured"
                      className="w-full h-full object-cover"
                    />
                  ) : (
                    <Webcam
                      ref={webcamRef}
                      screenshotFormat="image/jpeg"
                      className="w-full h-full object-cover"
                    />
                  )}
                </div>
                <div className="flex justify-center gap-4">
                  {capturedImage ? (
                    <>
                      <button
                        onClick={() => {
                          setCapturedImage(null);
                        }}
                        className="button-secondary"
                      >
                        <FaRedo /> Tekrar Çek
                      </button>
                      <button
                        onClick={performVirtualTryOn}
                        className="button-primary"
                      >
                        <FaMagic /> Kıyafet Dene
                      </button>
                    </>
                  ) : (
                    <button
                      onClick={capturePhoto}
                      className="button-primary"
                    >
                      <FaCamera /> Fotoğraf Çek
                    </button>
                  )}
                </div>
              </>
            )}

            {step === 3 && (
              <>
                <h2 className="page-title">Sonucu Görün</h2>
                <p className="page-subtitle">
                  Kıyafetin size nasıl yakıştığını inceleyin
                </p>
                <div className="relative">
                  {isLoading ? (
                    <Skeleton height={400} className="rounded-3xl" />
                  ) : virtualTryOnResult ? (
                    <img
                      src={virtualTryOnResult}
                      alt="Virtual Try-On Result"
                      className="result-image w-full"
                    />
                  ) : null}
                </div>
                <div className="flex justify-center mt-8 gap-4">
                  <button
                    onClick={() => {
                      setCapturedImage(null);
                      setVirtualTryOnResult(null);
                      setStep(2);
                    }}
                    className="button-secondary"
                  >
                    <FaRedo /> Yeni Fotoğraf
                  </button>
                </div>
              </>
            )}
          </motion.div>
        </AnimatePresence>
      </main>
    </div>
  );
};

export default App;
