import React, { useRef, useState } from 'react';
import Webcam from 'react-webcam';
import useStore from './store';
import { motion } from 'framer-motion';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaCamera, FaTshirt, FaRedo, FaCheck } from 'react-icons/fa';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';

const App: React.FC = () => {
  const { step, setStep } = useStore();
  const webcamRef = useRef<Webcam>(null);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const [virtualTryOnResult, setVirtualTryOnResult] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const capturePhoto = async () => {
    const imageSrc = webcamRef.current?.getScreenshot();
    if (!imageSrc) {
      toast.error('Fotoğraf çekilemedi. Lütfen kameranızı kontrol edin.');
      return;
    }
    setCapturedImage(imageSrc);
    setStep(2);
  };

  const performVirtualTryOn = async () => {
    if (!capturedImage) return;
    setIsLoading(true);
    try {
      const blob = await fetch(capturedImage).then(res => res.blob());
      const formData = new FormData();
      formData.append('file', blob, 'photo.jpg');

      const response = await fetch('http://localhost:8000/api/virtual-tryon', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Virtual try on işlemi başarısız oldu');
      }

      const blobResult = await response.blob();
      const base64Result = await new Promise<string>((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(blobResult);
        reader.onloadend = () => resolve(reader.result as string);
        reader.onerror = error => reject(error);
      });

      setVirtualTryOnResult(base64Result);
      setStep(3);
      toast.success('Kıyafet başarıyla denendi!');
    } catch (error) {
      toast.error('Virtual try on işlemi sırasında bir hata oluştu');
      console.error("Virtual try on hatası:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const containerVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5 } }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50 p-8">
      <ToastContainer position="top-right" autoClose={3000} />

      <motion.div 
        className="max-w-4xl mx-auto bg-white rounded-2xl shadow-xl p-8"
        initial="hidden"
        animate="visible"
        variants={containerVariants}
      >
        {step === 0 && (
          <div className="text-center space-y-6">
            <h1 className="text-4xl font-bold text-gray-800 mb-6">
              Virtual Try-On Deneyimine Hoş Geldiniz
            </h1>
            <div className="bg-blue-50 p-6 rounded-lg max-w-2xl mx-auto">
              <p className="text-gray-700 mb-4">
                Bu uygulama, kıyafetleri sanal olarak denemenize olanak sağlar. Devam etmek için KVKK şartlarını kabul etmeniz gerekmektedir.
              </p>
            </div>
            <motion.button 
              className="bg-blue-600 hover:bg-blue-700 text-white px-8 py-4 rounded-lg text-xl font-semibold flex items-center justify-center gap-2 mx-auto"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => setStep(1)}
            >
              <FaCheck className="text-xl" />
              KVKK Şartlarını Kabul Ediyorum
            </motion.button>
          </div>
        )}

        {step === 1 && (
          <div className="text-center space-y-6">
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Fotoğraf Çekin</h2>
            <div className="relative max-w-2xl mx-auto">
              <Webcam 
                audio={false} 
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                className="rounded-lg shadow-lg w-full"
              />
              <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2">
                <motion.button 
                  className="bg-green-500 hover:bg-green-600 text-white px-8 py-4 rounded-full text-xl font-semibold flex items-center gap-2"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={capturePhoto}
                >
                  <FaCamera className="text-xl" />
                  Fotoğraf Çek
                </motion.button>
              </div>
            </div>
          </div>
        )}

        {step === 2 && (
          <div className="text-center space-y-6">
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Virtual Try-On</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8 items-center">
              <div>
                {capturedImage && (
                  <img 
                    src={capturedImage} 
                    alt="Captured" 
                    className="rounded-lg shadow-md w-full"
                  />
                )}
              </div>
              <div className="space-y-4">
                <h3 className="text-xl font-semibold text-gray-700">Kıyafet Deneme</h3>
                <p className="text-gray-600">
                  Fotoğrafınız hazır! Şimdi kıyafet denemesi yapabilirsiniz.
                </p>
                <motion.button 
                  className="bg-purple-600 hover:bg-purple-700 text-white px-8 py-4 rounded-lg text-xl font-semibold flex items-center gap-2 mx-auto"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={performVirtualTryOn}
                  disabled={isLoading}
                >
                  {isLoading ? (
                    <>
                      <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white"></div>
                      İşleniyor...
                    </>
                  ) : (
                    <>
                      <FaTshirt className="text-xl" />
                      Kıyafet Dene
                    </>
                  )}
                </motion.button>
              </div>
            </div>
          </div>
        )}

        {step === 3 && (
          <div className="text-center space-y-6">
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Sonuç</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8 items-center">
              <div>
                {virtualTryOnResult ? (
                  <img 
                    src={virtualTryOnResult} 
                    alt="Virtual Try On Result" 
                    className="rounded-lg shadow-md w-full"
                  />
                ) : (
                  <Skeleton height={400} />
                )}
              </div>
              <div className="space-y-4">
                <h3 className="text-xl font-semibold text-gray-700">Kıyafet Denemesi Tamamlandı</h3>
                <p className="text-gray-600">
                  Başka bir kıyafet denemek ister misiniz?
                </p>
                <div className="flex gap-4 justify-center">
                  <motion.button 
                    className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg text-lg font-semibold flex items-center gap-2"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => setStep(1)}
                  >
                    <FaRedo className="text-lg" />
                    Yeni Fotoğraf Çek
                  </motion.button>
                </div>
              </div>
            </div>
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default App;
