import React from 'react';
import { ToastContainer, toast } from 'react-toastify';
import { FaCamera, FaTshirt, FaRedo, FaCheck, FaArrowRight } from 'react-icons/fa';
import Skeleton from 'react-loading-skeleton';
import 'react-toastify/dist/ReactToastify.css';
import 'react-loading-skeleton/dist/skeleton.css';

import useStore from './store';
import { useCamera } from './hooks/useCamera';
import { useVirtualTryOn } from './hooks/useVirtualTryOn';
import ErrorBoundary from './components/ErrorBoundary';
import { APP_STEPS, ERROR_MESSAGES, SUCCESS_MESSAGES } from './constants';

// --- İlerleme Adımları için Tip Tanımı ---
interface ProgressStep {
  step: number;
  label: string;
  icon: React.ReactNode;
}

const progressSteps: ProgressStep[] = [
  { step: APP_STEPS.WELCOME, label: "KVKK Onayı", icon: <FaCheck className="w-5 h-5" /> },
  { step: APP_STEPS.CAPTURE, label: "Fotoğraf Çekimi", icon: <FaCamera className="w-5 h-5" /> },
  { step: APP_STEPS.TRYON, label: "Kıyafet Deneme", icon: <FaTshirt className="w-5 h-5" /> },
];

// --- İlerleme Göstergesi Bileşeni ---
const ProgressIndicator: React.FC<{ currentStep: number }> = ({ currentStep }) => {
  return (
    <div className="mb-8 flex items-center justify-center gap-4">
      {progressSteps.map((item, idx) => (
        <React.Fragment key={item.step}>
          <div
            className={`flex flex-col items-center ${
              currentStep >= item.step ? 'text-indigo-600' : 'text-gray-400'
            }`}
          >
            <div
              className={`w-12 h-12 rounded-full flex items-center justify-center mb-2 transition-all ${
                currentStep >= item.step
                  ? 'bg-indigo-600 text-white shadow-lg'
                  : 'bg-gray-200 text-gray-400'
              }`}
            >
              {item.icon}
            </div>
            <span className="text-sm font-medium">{item.label}</span>
          </div>
          {idx < progressSteps.length - 1 && (
            <div
              className={`h-1 w-16 rounded transition-all ${
                currentStep > item.step ? 'bg-indigo-600' : 'bg-gray-200'
              }`}
            />
          )}
        </React.Fragment>
      ))}
    </div>
  );
};

// --- Ana Bileşen: App ---
const App: React.FC = () => {
  const { step, setStep } = useStore();
  const { 
    isLoading: cameraLoading, 
    error: cameraError, 
    photo,
    takePhoto,
    resetPhoto,
    webcamRef 
  } = useCamera();

  const {
    isLoading: tryOnLoading,
    error: tryOnError,
    resultImage,
    tryOnClothing,
    resetResult
  } = useVirtualTryOn();

  // Hata durumlarını kontrol et ve toast göster
  React.useEffect(() => {
    if (cameraError) {
      toast.error(cameraError);
    }
    if (tryOnError) {
      toast.error(tryOnError);
    }
  }, [cameraError, tryOnError]);

  // Fotoğraf çekme işlemi
  const handleCapture = async () => {
    const photoData = await takePhoto();
    if (photoData) {
      toast.success(SUCCESS_MESSAGES.PHOTO_CAPTURED);
    }
  };

  // Yeni fotoğraf çekme
  const handleRetake = () => {
    resetPhoto();
    resetResult();
  };

  // Virtual try-on işlemi
  const handleTryOn = async () => {
    if (photo) {
      setStep(APP_STEPS.TRYON);
      await tryOnClothing(photo);
    }
  };

  // Adım 1: Hoş Geldiniz / KVKK Onayı
  const renderWelcomeStep = () => (
    <div className="bg-white rounded-lg shadow-lg p-8 text-center">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">
        Akıllı Ayna Deneyimine Hoş Geldiniz
      </h1>
      <p className="text-gray-600 mb-8">
        Yapay zeka destekli sanal giyinme kabini ile alışveriş deneyiminizi dönüştürün.
        Seçtiğiniz kıyafetleri anında üzerinizde görün!
      </p>
      <button
        onClick={() => setStep(APP_STEPS.CAPTURE)}
        className="bg-indigo-600 text-white px-8 py-3 rounded-lg font-medium
                 hover:bg-indigo-700 transition-colors flex items-center justify-center gap-2
                 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
      >
        Başla <FaArrowRight />
      </button>
    </div>
  );

  // Adım 2: Fotoğraf Çekimi
  const renderCaptureStep = () => (
    <div className="bg-white rounded-lg shadow-lg p-8">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
        Fotoğrafınızı Çekin
      </h2>
      <div className="aspect-video rounded-lg overflow-hidden bg-gray-100 mb-6 shadow-inner">
        {cameraLoading ? (
          <Skeleton height="100%" />
        ) : photo ? (
          <img
            src={photo}
            alt="Captured"
            className="w-full h-full object-cover"
          />
        ) : (
          <div className="relative h-full">
            <div className="absolute inset-0 flex items-center justify-center">
              {webcamRef.current ? (
                <div className="webcam-container">
                  {webcamRef.current}
                </div>
              ) : (
                <div className="text-gray-500">Kamera başlatılıyor...</div>
              )}
            </div>
          </div>
        )}
      </div>
      <div className="flex justify-center gap-4">
        {photo ? (
          <>
            <button
              onClick={handleRetake}
              className="px-6 py-3 border-2 border-gray-300 rounded-lg font-medium
                       hover:border-gray-400 transition-colors flex items-center gap-2
                       hover:bg-gray-50"
            >
              <FaRedo /> Tekrar Çek
            </button>
            <button
              onClick={handleTryOn}
              className="bg-indigo-600 text-white px-6 py-3 rounded-lg font-medium
                       hover:bg-indigo-700 transition-colors flex items-center gap-2
                       shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
            >
              <FaTshirt /> Kıyafet Dene
            </button>
          </>
        ) : (
          <button
            onClick={handleCapture}
            disabled={cameraLoading}
            className="bg-indigo-600 text-white px-8 py-3 rounded-lg font-medium
                     hover:bg-indigo-700 transition-colors flex items-center gap-2
                     shadow-lg hover:shadow-xl transform hover:-translate-y-0.5
                     disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <FaCamera /> Fotoğraf Çek
          </button>
        )}
      </div>
    </div>
  );

  // Adım 3: Sanal Kıyafet Deneme
  const renderTryOnStep = () => (
    <div className="bg-white rounded-lg shadow-lg p-8">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
        İşte Yeni Görünümünüz!
      </h2>
      <div className="aspect-video rounded-lg overflow-hidden bg-gray-100 mb-6 shadow-inner">
        {tryOnLoading ? (
          <Skeleton height="100%" />
        ) : resultImage ? (
          <img
            src={resultImage}
            alt="Virtual Try-On Result"
            className="w-full h-full object-cover"
          />
        ) : null}
      </div>
      <div className="flex justify-center gap-4">
        <button
          onClick={() => {
            handleRetake();
            setStep(APP_STEPS.CAPTURE);
          }}
          className="bg-indigo-600 text-white px-8 py-3 rounded-lg font-medium
                   hover:bg-indigo-700 transition-colors flex items-center gap-2
                   shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
        >
          <FaCamera /> Yeni Fotoğraf Çek
        </button>
      </div>
    </div>
  );

  return (
    <ErrorBoundary>
      <div className="min-h-screen bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-4xl mx-auto">
          <ProgressIndicator currentStep={step} />
          {step === APP_STEPS.WELCOME && renderWelcomeStep()}
          {step === APP_STEPS.CAPTURE && renderCaptureStep()}
          {step === APP_STEPS.TRYON && renderTryOnStep()}
        </div>
        <ToastContainer position="bottom-center" />
      </div>
    </ErrorBoundary>
  );
};

export default App;
