import React, { useRef, useState } from 'react';
import Webcam from 'react-webcam';
import useStore from './store.ts';
import { motion } from 'framer-motion';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaCamera, FaTshirt, FaRedo, FaCheck, FaArrowRight } from 'react-icons/fa';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';

const App: React.FC = () => {
  const { step, setStep } = useStore();
  const webcamRef = useRef<Webcam>(null);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const [virtualTryOnResult, setVirtualTryOnResult] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const capturePhoto = async () => {
    const imageSrc = webcamRef.current?.getScreenshot();
    if (!imageSrc) {
      toast.error('Fotoğraf çekilemedi. Lütfen kameranızı kontrol edin.');
      return;
    }
    setCapturedImage(imageSrc);
    setStep(2);
  };

  const performVirtualTryOn = async () => {
    if (!capturedImage) return;
    setIsLoading(true);
    try {
      const blob = await fetch(capturedImage).then(res => res.blob());
      const formData = new FormData();
      formData.append('file', blob, 'photo.jpg');

      const response = await fetch('http://localhost:8000/api/virtual-tryon', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Virtual try on işlemi başarısız oldu');
      }

      const blobResult = await response.blob();
      const base64Result = await new Promise<string>((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(blobResult);
        reader.onloadend = () => resolve(reader.result as string);
        reader.onerror = error => reject(error);
      });

      setVirtualTryOnResult(base64Result);
      setStep(3);
      toast.success('Kıyafet başarıyla denendi!');
    } catch (error) {
      toast.error('Virtual try on işlemi sırasında bir hata oluştu');
      console.error("Virtual try on hatası:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const containerVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5 } }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-purple-50 p-8">
      <ToastContainer position="top-right" autoClose={3000} />

      <motion.div 
        className="max-w-6xl mx-auto"
        initial="hidden"
        animate="visible"
        variants={containerVariants}
      >
        {/* Progress Bar */}
        <div className="mb-8">
          <div className="flex justify-between items-center max-w-md mx-auto">
            {[1, 2, 3].map((num) => (
              <div key={num} className="flex items-center">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                  step >= num - 1 ? 'bg-indigo-600 text-white' : 'bg-gray-200'
                }`}>
                  {num}
                </div>
                {num < 3 && (
                  <div className={`w-24 h-1 ${
                    step >= num ? 'bg-indigo-600' : 'bg-gray-200'
                  }`} />
                )}
              </div>
            ))}
          </div>
        </div>

        {step === 0 && (
          <div className="glass-effect rounded-3xl p-12 text-center space-y-8">
            <h1 className="text-5xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 text-transparent bg-clip-text mb-6">
              Akıllı Ayna Deneyimine Hoş Geldiniz
            </h1>
            <div className="max-w-2xl mx-auto space-y-6">
              <p className="text-lg text-gray-700 leading-relaxed">
                Yapay zeka destekli sanal giyinme kabini ile kıyafetleri üzerinizde görün.
                Hiç olmadığı kadar gerçekçi ve hızlı bir alışveriş deneyimi için hazır mısınız?
              </p>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-center">
                <div className="p-6 rounded-xl bg-white shadow-custom">
                  <div className="text-3xl text-indigo-600 mb-4">1</div>
                  <h3 className="font-semibold mb-2">Fotoğraf Çekin</h3>
                  <p className="text-gray-600">Kameranızla hızlıca bir fotoğraf çekin</p>
                </div>
                <div className="p-6 rounded-xl bg-white shadow-custom">
                  <div className="text-3xl text-indigo-600 mb-4">2</div>
                  <h3 className="font-semibold mb-2">Kıyafet Seçin</h3>
                  <p className="text-gray-600">Denemek istediğiniz kıyafeti seçin</p>
                </div>
                <div className="p-6 rounded-xl bg-white shadow-custom">
                  <div className="text-3xl text-indigo-600 mb-4">3</div>
                  <h3 className="font-semibold mb-2">Sonucu Görün</h3>
                  <p className="text-gray-600">Kıyafetin üzerinizdeki görüntüsünü inceleyin</p>
                </div>
              </div>
            </div>
            <motion.button 
              className="button-primary group"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => setStep(1)}
            >
              <span className="flex items-center gap-3">
                Başlayalım
                <FaArrowRight className="group-hover:translate-x-1 transition-transform" />
              </span>
            </motion.button>
          </div>
        )}

        {step === 1 && (
          <div className="glass-effect rounded-3xl p-12 space-y-8">
            <h2 className="text-4xl font-bold text-center text-gray-800 mb-8">
              Fotoğrafınızı Çekin
            </h2>
            <div className="max-w-4xl mx-auto">
              <div className="webcam-container">
                <Webcam 
                  audio={false} 
                  ref={webcamRef}
                  screenshotFormat="image/jpeg"
                  className="w-full h-full object-cover"
                />
              </div>
              <div className="mt-8 flex justify-center">
                <motion.button 
                  className="button-primary group"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={capturePhoto}
                >
                  <span className="flex items-center gap-3">
                    <FaCamera className="text-xl" />
                    Fotoğraf Çek
                  </span>
                </motion.button>
              </div>
            </div>
          </div>
        )}

        {step === 2 && (
          <div className="glass-effect rounded-3xl p-12 space-y-8">
            <h2 className="text-4xl font-bold text-center text-gray-800 mb-8">
              Kıyafet Deneme
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-12 items-center">
              <div className="space-y-4">
                {capturedImage && (
                  <img 
                    src={capturedImage} 
                    alt="Captured" 
                    className="result-image w-full"
                  />
                )}
                <p className="text-center text-gray-600">Çekilen Fotoğraf</p>
              </div>
              <div className="space-y-6">
                <div className="p-6 rounded-xl bg-white shadow-custom">
                  <h3 className="text-2xl font-semibold mb-4">Kıyafet Seçimi</h3>
                  <p className="text-gray-600 mb-6">
                    Fotoğrafınız hazır! Şimdi AI modelimiz seçtiğiniz kıyafeti üzerinize uygulayacak.
                  </p>
                  <motion.button 
                    className="button-primary w-full group"
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    onClick={performVirtualTryOn}
                    disabled={isLoading}
                  >
                    {isLoading ? (
                      <div className="flex items-center justify-center gap-3">
                        <div className="loading-spinner" />
                        İşleniyor...
                      </div>
                    ) : (
                      <span className="flex items-center justify-center gap-3">
                        <FaTshirt className="text-xl" />
                        Kıyafeti Dene
                      </span>
                    )}
                  </motion.button>
                </div>
              </div>
            </div>
          </div>
        )}

        {step === 3 && (
          <div className="glass-effect rounded-3xl p-12 space-y-8">
            <h2 className="text-4xl font-bold text-center text-gray-800 mb-8">
              İşte Sonucunuz!
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-12 items-center">
              <div className="space-y-4">
                {virtualTryOnResult ? (
                  <img 
                    src={virtualTryOnResult} 
                    alt="Virtual Try On Result" 
                    className="result-image w-full"
                  />
                ) : (
                  <Skeleton height={400} />
                )}
                <p className="text-center text-gray-600">Kıyafet Denenmiş Hali</p>
              </div>
              <div className="space-y-6">
                <div className="p-6 rounded-xl bg-white shadow-custom">
                  <h3 className="text-2xl font-semibold mb-4">Harika Görünüyor!</h3>
                  <p className="text-gray-600 mb-6">
                    Kıyafet denemesi tamamlandı. Başka bir kıyafet denemek ister misiniz?
                  </p>
                  <div className="flex gap-4">
                    <motion.button 
                      className="button-primary flex-1 group"
                      whileHover={{ scale: 1.02 }}
                      whileTap={{ scale: 0.98 }}
                      onClick={() => setStep(1)}
                    >
                      <span className="flex items-center justify-center gap-2">
                        <FaCamera className="text-lg" />
                        Yeni Fotoğraf
                      </span>
                    </motion.button>
                    <motion.button 
                      className="button-secondary flex-1 group"
                      whileHover={{ scale: 1.02 }}
                      whileTap={{ scale: 0.98 }}
                      onClick={() => setStep(2)}
                    >
                      <span className="flex items-center justify-center gap-2">
                        <FaTshirt className="text-lg" />
                        Başka Kıyafet
                      </span>
                    </motion.button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default App;
