import React, { useRef, useState, useCallback } from 'react';
import Webcam from 'react-webcam';

function WebcamCapture({ onCapture }) {
  const webcamRef = useRef(null);
  const [error, setError] = useState(null);
  const [isCaptured, setIsCaptured] = useState(false);

  const videoConstraints = {
    width: 720,
    height: 720,
    facingMode: "user"
  };

  const capture = useCallback(() => {
    try {
      const imageSrc = webcamRef.current.getScreenshot();
      if (imageSrc) {
        // Base64 formatında fotoğrafı localStorage'a kaydet
        localStorage.setItem('lastCapturedImage', imageSrc);
        onCapture(imageSrc);
        setIsCaptured(true);
        setError(null);
      } else {
        setError('Fotoğraf çekilemedi. Lütfen tekrar deneyin.');
      }
    } catch (err) {
      console.error('Capture error:', err);
      setError('Fotoğraf çekilirken bir hata oluştu.');
    }
  }, [onCapture]);

  const handleRetake = () => {
    setIsCaptured(false);
    localStorage.removeItem('lastCapturedImage');
    onCapture(null);
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-soft">
      <h2 className="text-lg font-bold mb-4">Fotoğraf Çek</h2>

      {error && (
        <div className="mb-4 text-red-600 bg-red-50 p-3 rounded">
          {error}
        </div>
      )}

      <div className="relative">
        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          videoConstraints={videoConstraints}
          className="w-full rounded-lg"
        />

        <div className="mt-4 flex justify-center space-x-4">
          {!isCaptured ? (
            <button
              onClick={capture}
              className="bg-primary-500 hover:bg-primary-600 text-white px-6 py-2 rounded-lg transition-colors"
            >
              Fotoğraf Çek
            </button>
          ) : (
            <button
              onClick={handleRetake}
              className="bg-secondary-500 hover:bg-secondary-600 text-white px-6 py-2 rounded-lg transition-colors"
            >
              Tekrar Çek
            </button>
          )}
        </div>
      </div>

      {isCaptured && (
        <div className="mt-4 text-center text-sm text-primary-600">
          Fotoğraf başarıyla çekildi! ✓
        </div>
      )}
    </div>
  );
}

export default WebcamCapture;
