import React, { useState } from 'react';
import axios from 'axios';

function VirtualTryOn({ suggestions, onTryOnComplete }) {
  const [loading, setLoading] = useState(false);

  const handleTryOn = async (item) => {
    setLoading(true);
    try {
      // Kolors API'ye gönderilecek payload
      // Gerçek senaryoda kullanıcı resmi base64 (photoData) + kıyafet resmi vb. eklemeniz gerekir.
      const payload = {
        model_name: "kolors-virtual-try-on-v1",
        human_image: "base64_or_url_of_user_image",
        cloth_image: "base64_or_url_of_cloth_image", // Örn. item.color'a göre
      };
      const response = await axios.post("http://localhost:8000/images/kolors-virtual-try-on", payload);
      onTryOnComplete(response.data); // { virtual_image_url: ... }
    } catch (error) {
      console.error("Virtual Try-On hata:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white p-4 rounded shadow-md">
      <h2 className="text-lg font-bold mb-2">Virtual Try-On</h2>
      {suggestions.length > 0 ? (
        suggestions.map((item) => (
          <button
            key={item.id}
            className="bg-purple-500 text-white px-4 py-2 m-2 rounded"
            onClick={() => handleTryOn(item)}
          >
            {item.name} - {item.color}
          </button>
        ))
      ) : (
        <p>Henüz öneri yok.</p>
      )}
      {loading && <p>Virtual Try-On işleniyor...</p>}
    </div>
  );
}

export default VirtualTryOn;
