import React, { useEffect } from 'react';
import axios from 'axios';

function ProductSuggestions({ photoData, weatherData, onSuggestionsLoaded }) {
  useEffect(() => {
    const analyzeImage = async () => {
      if (!photoData) return;
      try {
        const response = await axios.post("http://localhost:8000/analyze", {
          image: photoData,
        });
        onSuggestionsLoaded(response.data.suggestions);
      } catch (error) {
        console.error("Fotoğraf analizi yapılamadı:", error);
      }
    };
    analyzeImage();
  }, [photoData, onSuggestionsLoaded]);

  return (
    <div className="bg-white p-4 rounded shadow-md">
      <h2 className="text-lg font-bold mb-2">Ürün Önerileri</h2>
      {weatherData && (
        <p>Sıcaklık: {weatherData.temperature} °C (Burada hava durumuna göre özel öneriler yapabilirsiniz.)</p>
      )}
    </div>
  );
}

export default ProductSuggestions;
