import React, { useEffect, useState } from 'react';
import axios from 'axios';

function WeatherDisplay({ onWeatherLoaded }) {
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(async (position) => {
        setLoading(true);
        try {
          const lat = position.coords.latitude;
          const lon = position.coords.longitude;
          const response = await axios.get(
            `http://localhost:8000/weather?lat=${lat}&lon=${lon}`
          );
          setWeather(response.data);
          onWeatherLoaded(response.data);
        } catch (error) {
          console.error("Hava durumu alınamadı:", error);
        } finally {
          setLoading(false);
        }
      });
    } else {
      console.error("Geolocation desteklenmiyor.");
    }
  }, [onWeatherLoaded]);

  if (loading) {
    return <div>Hava durumu yükleniyor...</div>;
  }

  if (!weather) {
    return <div>Hava durumu bilgisi bulunamadı.</div>;
  }

  return (
    <div className="bg-white p-4 rounded shadow-md">
      <h2 className="text-lg font-bold mb-2">Hava Durumu</h2>
      <p>Sıcaklık: {weather.temperature} °C</p>
      <p>Nem: {weather.humidity} %</p>
      <p>Rüzgar: {weather.wind_speed} m/s</p>
    </div>
  );
}

export default WeatherDisplay;
